/* header */
header {
  border-bottom: 1px solid;
}

/* header right side section */
.navUser {
  ul {
    padding: 10px 0;
    li {
      @media (min-width: 801px) {
        padding: 0 15px;
      }
      .label {
        font-size: 10px;
        margin-bottom: 5px;
        display: block;
        @media (max-width: 800px) {
          display: none;
        }
      }
      a {
        text-align: center;
      }
      &:last-child {
        margin-left: 0;
      }
      .navUser-action {
        padding: 0;
      }
    }
  }
  .contact-header {
    float: right;
    margin-top: 5px;
    display: inline-block;
    width: 100%;
    text-align: right;
    @media (max-width: 800px) {
      display: none;
    }
    p {
      font-size: 14px;
      margin: 0;
      strong {
        font-size: 18px;
      }
    }
  }
  .desktop-search {
    @media (min-width: 800px) {
      display: none;
    }
  }
}
/* responsive navegation  */
.navPages-container.is-open {
  height: 400px;
  .navPage-subMenu.is-open {
    .navPage-subMenu-item {
      padding: 0;
      padding-left: 5px;
      a {
        font-size: 14px;
        font-size: normal;
      }
    }
  }
  .icon.navPages-action-moreIcon.main-moreicon.is-open {
    svg {
      transform: rotate(90deg);
    }
  }
}
@media (max-width: 360px) {
  #logo {
    margin-left: -25px;
  }
}
@media screen and (min-device-width: 800px) and (max-device-width: 908px) {
  .navUser ul li {
    padding: 0 10px;
  }
}

.navUser-item--cart {
  .navUser-action {
    padding: 0;
    svg {
      display: none;
    }
    .cart-icon {
      width: 24px;
      height: 24px;
      background: url(../img/shopping-cart-icon.svg) no-repeat center center;
      display: inline-block;
      vertical-align: middle;
    }
  }
}

@media (max-width: 1409px) {
  .navUser-action {
    padding: 0;
  }
}
@media (max-width: 800px) {
  .navUser-item--cart {
    .countPill {
      top: 0;
    }
  }
}

/* search design for desktop */

.search-section {
  @media (max-width: 800px) {
    display: none;
  }
  #search.desktop-search {
    width: 65%;
    display: block;
    margin: 0 auto;
  }
  .d-search {
    display: none;
  }
  #search {
    position: relative;
    opacity: 1;
    transform: none;
    background: none;
    width: 100%;
    padding: 0;
    form {
      border-bottom: 1px solid;
      .closebtn.close-nav {
        display: none;
      }
      .wb-search-btn {
        background: none;
        svg {
          fill: #000;
        }
      }
    }
  }
  .form-input {
    color: #000;
  }
}

/* navbar design */

.catcushead {
  border-top: none;
  @media (min-width: 801px) {
    padding-top: 20px;
  }
}

/* header nav link */
.catcushead {
  .navPages {
    ul {
      li {
        &:nth-child(even) {
          display: none;
        }
        &:nth-child(6) {
          display: none;
        }
        &:nth-child(7) {
          display: none;
        }
        a {
          padding: 15px 25px;
        }
      }
    }
  }
}

/* hero banner */

.heroCarousel {
  .heroCarousel-content {
    background: rgba(255, 255, 255, 0.25);
    left: 0;
    right: 0;
    width: 40%;
    border-radius: 0;
    .heroCarousel-title {
      font-size: 31px;
      text-align: center;
      padding-top: 1rem;
      font-family: 'Trebuchet MS';
      font-weight: bold;
      @media screen and (max-width: 479px) {
        font-size: 22px;
      }
    }
    .heroCarousel-description {
      font-size: 18px;
      line-height: normal;
      text-align: center;
      color: rgb(77, 77, 77);
      @media screen and (max-width: 479px) {
        font-size: 14px;
      }
    }
    .heroCarousel-action {
      display: flex;
      justify-content: center;
      align-items: center;
      text-align: center;
      width: fit-content;
      width: -moz-fit-content;
      margin: auto;
    //   background: #5f8278;
      background: #14677E;
      border: none;
      &:hover {
        background: #ce8359;
      }
    }
    @media screen and (max-width: 767px) {
      width: 80%;
      border-radius: 5px;
    }
  }
}

/* feature content section */

.feature-product-section {
  .featured {
    h2 {
      &.page-heading {
        padding-bottom: 20px;
      }
    }
  }
}

/* footer */

.webi-subscribe {
  p {
    display: none;
  }
}

.foot-social {
  ul {
    li {
      margin: 0 10px 0 0;
      padding: 5px 0;
      width: auto;
      float: left;
      list-style: none;
    }
  }
  .icon-social {
    display: inline-block;
    text-align: left;
    text-indent: -9999px;
    vertical-align: middle;
    width: 25px;
    height: 25px;
    background: url(../img/sprite-social-default.png) no-repeat;
    &.icon-facebook {
      background-position: 0 0;
    }
    &.icon-twitter {
      background-position: 0 -120px;
    }
    &.icon-pinterest {
      background-position: 0 -160px;
    }
  }
}
.foot-newsletter {
  padding-bottom: 3rem;
  margin-bottom: 40px;
  border-bottom: 1px solid #fff;
  .footer-info-heading {
    display: inline-block;
    margin: 0;
  }
  .footer-info-col {
    text-align: center;
    margin: 0;
    #nl_email {
      border-radius: 3px 0 0 3px !important;
    }
  }
}
@media (max-width: 767px) {
  #descwrapper {
    margin-top: 10px;
  }
}
@media (max-width: 575px) {
  .form-prefixPostfix {
    display: block;
  }
