    margin: 0;
    color: #999;
    font-size: 12px;
    font-weight: 600;
  }
  a {
    color: #999;
    font-size: 12px;
    font-weight: 600;
    &:hover {
      color: #ce8359;
    }
  }
}

.d-inline-block {
  display: inline-block;
}

/* service css */
.service-section {
  background: #5f8278;
  margin-top: 50px;
  .deliveryinfo {
    &.row {
      margin-left: -10px;
      margin-right: -10px;
    }
    .service-cont {
      padding: 0px 10px;
    }
  }
}
.deliveryinfo {
  background: #5f8278;
  padding: 45px 0;
  align-items: initial;
  border-radius: 0;
  .service-cont {
    padding: 0 5px;
    @media screen and (min-device-width: 768px) and (max-device-width: 1210px) {
      max-width: 50%;
      flex: 0 0 50%;
      margin: 15px 0;
    }
  }
  .service {
    svg {
      width: 40px;
      height: 40px;
      margin-right: 0;
    }
    .caption-box {
      display: flex;
      align-items: center;
      min-height: 45px;
      h4 {
        display: inline-block;
        width: calc(100% - 46px);
        margin: 0;
      }
      img {
        margin-right: 10px;
        filter: invert(1);
      }
    }
  }
  h4 {
