/* feature-categories-section */

.feature-categories-section {
  .row {
    &.maxwidth90 {
      @media (min-width: 801px) {
        max-width: 90%;
        margin: 0 auto;
      }
    }
    .hover-change {
      @media (min-width: 801px) {
        padding: 0 10px;
      }
      @media (max-width: 767px) {
        margin-bottom: 20px;
      }
      &:nth-child(even) {
        h4 {
          background: #5c8279;
        }
        &:hover {
          h4 {
            background: rgba(92, 130, 121, 0.8);
          }
        }
      }  
      &:nth-child(odd) {
        h4 {
          background: #ce8359;
        }
        &:hover {
          h4 {
            background: rgba(206, 131, 89, 0.8);
          }
        }
      }  
    }
    a {
      position: relative;
    }
    img {
      position: relative;
      width: 100%;
      border-radius: 20px 20px 8px 8px;
      @media (max-width: 767px) {
        width: 100%;
      }
    }
    h4 {
      background: #5c8279;
      font-size: 18px;
      margin: 0;
      width: 100%;
      position: relative;
      padding: 13px 10px 7px;
      color: #fff;
      text-align: center;
      transition: 0.3s all ease;
      border-radius: 0 0 8px 8px;
      position: absolute;
      bottom: 0;
      left: 0;
      right: 0;
      font-family: 'Abadi MT Pro';
      font-weight: bold;
      @media (max-width: 1200px) {
        font-size: 18px;
      }
      @media (max-width: 920px) {
        font-size: 13px;
      }
    }
  }
}

